export function format(js) {
	return js |> parse |> filter |> strip |> clean |> indent;
}

const options = {
	useTabs: true,
	singleQuote: true,
	printWidth: 80
};

function parse(js) {
	return prettier.format(js, {
		...options,
		parser(text, { babylon }) {
			let ast;
			try {
				ast = babylon(text);
			} catch (exception) {
				console.error(exception);
				ast = babylon('');
			}
			return mark(ast);
		}
	});
}

function mark(ast) {
	const jsdocs = com => com.value.startsWith('*');
	const header = com => com.value.endsWith('......'); 
	const others = com => !jsdocs(com) && !header(com);
	const markit = com => (com.value = 'DELETE');
	ast.comments.filter(others).forEach(markit);
	return ast;
}

function filter(js) {
	return eachline(js, lines => {
		return lines.filter(line => {
			return (line = line.trim()) !== '' && line !== '';
		});
	});
}

function strip(js) {
	const block = /\/\*\DELETE\*\//g;
	const lines = /\/\/\DELETE/g;
	return [block, lines].reduce((result, regex) => {
		return result.replace(regex, '');
	}, js);
}

function clean(js) {
	let was = false;
	const ok = line => (was = line !== '' || !was);
	return eachline(js, lines => {
		return lines.filter(ok);
	});
}

function indent(js, tabs = '') {
	const empty = line => line === '';
	const level = line => /^\t*/.exec(line)[0];
	return eachline(js, lines => {
		return lines.map(line => {
			tabs = empty(line) ? tabs : level(line);
			return empty(line) ? tabs : line;
		});
	});
}

import fs from 'fs';
import path from 'path';
import { parse } from './parsedocument';
import { index } from './indexfiletree';

export function compile(files, options = {}) {
	files = Array.isArray(files) ? files : [files];
	const docs = begin(Object.create(null), files, options);
	const tree = index(Object.keys(docs));
	return { tree, docs };
}

function begin(result, files, options) {
	files.forEach(file => {
		const text = read(file);
		const type = path.extname(file);
		const title = path.basename(file, type);
		result[file] = parse(type, title, text);
	});
	return result;
}

function read(filepath) {
	return fs.readFileSync(filepath, { encoding: 'UTF-8' });
}

export function index(sources) {
	const steps = [step1, step2, step3, step4, step5];
	return new TreeModel(
		steps.reduce((input, nextstep) => {
			return nextstep(input);
		}, sources)
	);
}

function step1(sources) {
	const map = Object.create(null);
	function next(source, parts, map) {
		let part = parts.shift();
		if (!parts.length) {
			map[part] = source;
		} else {
			map[part] = map[part] || Object.create(null);
			next(source, parts, map[part]);
		}
	}
	sources.forEach(src => next(src, src.split('/'), map));
	return map;
}
function step2(map) {
	const root = { name: '/', nodes: [] };
	return (function next(map, parent, level) {
		Object.keys(map).forEach(key => {
			let val = map[key];
			let node = { name: key };
			if (typeof val === 'object') {
				node.nodes = [];
				node.open = level < 1;
				next(val, node, level + 1);
			} else {
				node.src = val;
			}
			parent.nodes.push(node);
		});
		return parent;
	})(map, root, 0);
}
function step3(folder) {
	let is,
		nodes = folder.nodes;
	const alpha = (n1, n2) => {
		return n1.name < n2.name ? -1 : n1.name > n2.name ? 1 : 0;
	};
	folder.nodes = nodes
		.filter(node => {
			return (is = node.nodes) ? step3(node) : false;
		})
		.sort(alpha)
		.concat(nodes.filter(node => !node.nodes).sort(alpha));
	return folder;
}

function step4(folder) {
	while (folder.nodes.length === 1 && folder.nodes[0].nodes) {
		folder = folder.nodes[0];
	}
	return folder;
}

function step5(folder) {
	folder.filenames =
		folder.filenames ||
		folder.nodes
			.map(node => {
				return node.nodes ? step5(node).filenames : node.name.toLowerCase();
			})
			.join(' ');
	return folder;
}

import { format } from './formatscript';
import JSDocModel from '../models/docs/JSDocModel';
import MDDocModel from '../models/docs/MDDocModel';
import EDBMLDocModel from '../models/docs/EDBMLDocModel';

export function parse(type, title, text) {
	switch (type) {
		case '.js':
			return new JSDocModel({
				title: title,
				chapters: getchapters(format(text))
			});
			break;
		case '.md':
			return new MDDocModel({
				title: title,
				markup: '###TODO'
			});
			break;
		case '.edbml':
			return new EDBMLDocModel({
				title: title,
				chapters: getchapters(text)
			});
			break;
	}
}

function getchapters(source) {
	let comment = false;
	let chapter = null;
	let section = null;
	let chapters = [];
	let sections = [];
	const nextchapter = (title = '', tabs = '') => {
		if (chapter) {
			chapter.sections = sections;
			chapters.push(chapter);
		}
		chapter = {
			title: title,
			tabs: tabs
		};
		sections = [];
	};
	const nextsection = line => {
		if (section && interesting(section)) {
			sections.push(section);
		}
		section = {
			line: line || 0,
			tags: [],
			tabs: '',
			desc: '',
			code: ''
		};
	};
	const indentlevel = line => {
		return /^\t*/.exec(line)[0];
	};
	nextsection();
	nextchapter();
	source.split('\n').forEach((line, index) => {
		let md,
			trim = line.trim();
		if (comment) {
			if (trim.startsWith('*/')) {
				comment = false;
			} else {
				if (trim === '*') {
					section.desc += '\n\n';
				} else {
					md = line.replace(GUTTER, '');
					switch (md[0]) {
						case '@':
							const json = gettag(md);
							section.tags.push(json);
							break;
						case '#':
							section.desc += '\n' + md + '\n\n';
							break;
						default:
							if (!md.match(LETTER)) {
								section.desc += '\n';
							}
							section.desc += encode(md) + SPACE;
							break;
					}
				}
			}
		} else {
			if (line.match(CHAPTER)) {
				let title = TITLE.exec(line);
				let tabs = indentlevel(line);
				section.tabs = tabs;
				nextsection(index);
				nextchapter(title ? title[0] : '', tabs);
				section.code += HELLO.exec(line);
			} else {
				if (trim.startsWith('/**')) {
					section.tabs = indentlevel(line);
					comment = true;
					nextsection(index);
				} else {
					section.code += line + '\n';
				}
			}
		}
	});
	nextsection();
	nextchapter();
	return chapters;
}

function interesting({ code, desc }) {
	return !!(code.trim() + desc.trim()).length;
}

function gettag(md) {
	const parts = md.split(/ +/); 
	const json = {
		name: '',
		type: '',
		desc: '', 
		text: ''
	};
	parts.forEach(part => {
		switch (part[0]) {
			case '@':
				json.name = part;
				break;
			case '{':
				json.type = part; 
				break;
			default:
				if (json.type.endsWith('}')) {
					hello(json, part);
				} else {
					json.type += part;
				}
				break;
		}
	});
	json.text = json.text.trim(); 
	return json;
}

function hello(json, part) {
	switch (json.name) {
		case '@return':
		case '@returns':
			json.text += part + ' ';
			break;
		case '@param':
			if (json.desc) {
				json.text += part + ' ';
			} else {
				json.desc += part;
			}
			break;
	}
}

function encode(string) {
	return string
		.replace(/&(?!\w+;)/g, '&amp;')
		.replace(/</g, '&lt;')
		.replace(/>/g, '&gt;');
}

