import { Model } from 'dataplastique';
import { Collection } from 'dataplastique';
import NodeModel from './NodeModel';
import FileModel from './FileModel';

static model() {
	return {
		open: Boolean,
		filenames: String, 
		nodes: Collection.$of(json => (json.nodes ? FolderModel : FileModel))
	};
}

onconstruct() {
	super.onconstruct();
	this.nodes.forEach(node => (node.$parent = this));
	this.open = false; 
}

matches(term) {
	return this.filenames.includes(term.trim().toLowerCase());
}

	classname() {
		return (
			'folder' + (this.open ? ' open' : '') + (this.focused ? ' focused' : '')
		);
	}
}

static model() {
	return {
		name: String,
		result: Boolean,
		focused: Boolean,
		$parent: NodeModel
	};
}

prev() {
	return this.sibling(-1);
}

next() {
	return this.sibling(+1);
}

	sibling(index) {
		if (this.$parent) {
			let siblings = this.$parent.nodes;
			let i = siblings.indexOf(this);
			let n = siblings[i + index];
			return n || null;
		}
		return null;
	}
}

static model() {
	return {
		search: String,
		focusnode: NodeModel,
		selectednode: NodeModel
	};
}

onconstruct() {
	super.onconstruct();
	this.search = '';
	this.addObserver(this);
}

onpoke(model, name, newval, oldval = '') {
	if (name === 'search') {
		newval = newval.trim();
		oldval = oldval.trim();
		if (newval.length !== oldval.length) {
			this.search = newval;
		} else {
			this._focusdefault(newval);
		}
	}
}

onload(src) {
	let node;
	if (src && (node = node = this._getnode(src))) {
		this._select(node);
		this._focus(node);
	}
	return this;
}

movefocus(key) {
	let next = this._nextfocus(key, this.focusnode);
	if (next) {
		this._focus(next);
	}
}

_select(newnode) {
	let oldnode;
	if ((oldnode = this.selectednode)) {
		oldnode.selected = false;
	}
	this.selectednode = newnode;
	newnode.selected = true;
}

_focus(newnode) {
	let oldnode;
	if ((oldnode = this.focusnode)) {
		oldnode.focused = false;
	}
	if ((this.focusnode = newnode || null)) {
		newnode.focused = true;
		while ((newnode = newnode.$parent)) {
			newnode.open = true;
		}
	}
}

_focusdefault(query) {
	let files = this._matchingfiles(query);
	if (files.indexOf(this.focusnode) === -1) {
		this._focus(files[0]);
	}
}

_nextfocus(key, node) {
	let next;
	let nodes;
	let q = this.search;
	const isopen = node => node.nodes && (node.open || (q && node.matches(q)));
	const hasnodes = node => node.nodes && node.nodes.length;
	const hasparent = node => node.$parent && node.$parent !== this;
	switch (key) {
		case 'Up':
			if ((next = node.prev())) {
				while (isopen(next) && hasnodes(next)) {
					nodes = next.nodes;
					next = nodes[nodes.length - 1];
				}
			} else if (hasparent(node)) {
				next = node.$parent;
			}
			break;
		case 'Down':
			if (isopen(node)) {
				next = node.nodes[0];
			} else {
				next = node.next();
				while (!next && hasparent(node)) {
					next = node.$parent.next();
					node = node.$parent;
				}
			}
			break;
		case 'Left':
			if (node.nodes && isopen(node)) {
				node.open = false;
			}
			break;
		case 'Right':
			if (node.nodes && !isopen(node)) {
				node.open = true;
			}
			break;
	}
	if (next && q) {
		if (!next.matches(q)) {
			next = this._nextfocus(key, next);
		}
	}
	return next;
}

_getnode(src) {
	let hit = null;
	(function search(folder) {
		folder.nodes.every(node => {
			if (node.src === src) {
				hit = node;
			} else if (node.nodes) {
				search(node);
			}
			return !hit;
		});
	})(this);
	return hit;
}

	_matchingfiles(query) {
		let files = [];
		(function index(folder) {
			folder.nodes.forEach(node => {
				if (node.nodes) {
					index(node);
				} else if (node.matches(query)) {
					files.push(node);
				}
			});
		})(this);
		return files;
	}
}

export default function(doc) {
	<article>
		if (doc) {
			<h1>${doc.title}</h1>
			const syntax = hilite(doc.type);
			switch(doc.type) {
				case 'text/javascript':
				case 'text/edbml':
					renderScript(doc, syntax);
					break;
			}
		}
	</article>
}

function renderChapter({ title, sections, tabs }) {
	if(title) {
		renderSection({ desc: '## ' + title, tabs });
	}
	sections.forEach(renderSection);
}

function renderSection({ desc, code, tags, line, tabs }) {
	@data-line = line;
	<section @data-line>
		<div class="docs">
			<div class="desc">
				out(markup(desc));
			</div>
			renderTags(tags);
		</div>
		<div class="code">
			<code>
				out(code ? syntax(code) : '');
				<span class="indent">
					out(tabs);
				</span>
			</code>
		</div>
	</section>
}

function renderTags(tags) {
	if(tags && tags.length) {
		<ol class="tags">
			tags.forEach(renderTag);
		</ol>
	}
}

	function renderTag({name, type, desc, text}) {
		<li class="tag">
			<span class="name">${name}</span>
			if(type) {
				<span class="type">${type}</span>
			}
			if (desc) {
				<span class="desc">
					out(markup(desc));
				</span>
			}
			if (text) {
				<span class="text">
					out(markup(text));
				</span>
			}
		</li>
	}
}

import edbml from './CodeSpirit.edbml.js';
import DocModel from '../../models/docs/DocModel';
import MDDocModel from '../../models/docs/MDDocModel';
import EDBMLDocModel from '../../models/docs/EDBMLDocModel';
import JSDocModel from '../../models/docs/JSDocModel';

export default function CodeSpirit({ script, io, dom, element }) {
	return {
		onattach() {
			script.load(edbml);
			io.on({ MDDocModel, EDBMLDocModel, JSDocModel }, doc => {
				script.run(doc);
			});
		},
		onrun() {
			element.scrollTop = 0;
			if (!lineart.done) {
				lineart(dom.q('.code code'));
			}
		}
	};
}

function lineart(code) {
	if (code) {
		addrule(getrule(getresolver(code)));
		lineart.done = true;
	}
}

function getresolver(code) {
	const props = getComputedStyle(code, null);
	const getit = name => props.getPropertyValue(name);
	return name => getit(name) || getit('-moz-' + name);
}

function getrule(resolve) {
	return `.indent, .code code:after { background: url("${draw(
		fontshorthand(resolve),
		decorationals(resolve)
	)}"); }`;
}

function addrule(rule) {
	const sheet = document.styleSheets[0];
	sheet.insertRule(rule, sheet.cssRules.length);
}

function draw(font, [linecolor, tabsize]) {
	const canvas = document.createElement('canvas');
	const context = canvas.getContext('2d');
	context.font = font;
	(tabwidth => {
		canvas.height = 100;
		canvas.width = tabwidth * tabsize;
		context.fillStyle = linecolor;
		context.fillRect(0, 0, 1, 100);
	})(context.measureText('\t').width);
	return canvas.toDataURL('image/png');
}

function fontshorthand(resolve) {
	return ['font-weight', 'font-style', 'font-size', 'font-family']
		.map(resolve)
		.join(' ');
}

function decorationals(resolve) {
	return ['border-left-color', 'tab-size'].map(resolve);
}

export default function hilite(type) {
	return code => format(Prism.highlight(code.trimRight(), lookup(type)));
}

function lookup(type) {
	switch (type) {
		case 'text/javascript':
			return Prism.languages.javascript;
		case 'text/edbml':
			return Prism.languages.jsx;
		default:
			return Prism.languages.text;
	}
}

function format(html) {
	return html
		.split('\n')
		.map(indent)
		.map(inline)
		.join('');
}

export default function markup(markdown) {
	return converter.makeHtml(markdown || '');
}

import JSDocModel from '../../models/docs/JSDocModel';
import MDDocModel from '../../models/docs/MDDocModel';
import EDBMLDocModel from '../../models/docs/EDBMLDocModel';
import TreeModel from '../../models/tree/TreeModel';

export default function RootSpirit() {
	const hash = (location.hash || '#').slice(1);
	fetch('index.json').then(response => {
		if (response.ok) {
			addEventListener('hashchange', () => {
				load(location.hash.slice(1));
			});
			response
				.json()
				.then(json => {
					docs = new Map(Object.entries(json.docs));
					tree = new TreeModel(json.tree).onload(hash).output();
				})
				.then(() => {
					if (hash) {
						load(hash);
					}
				})
				.catch(x => {
					console.error(x);
