	return `<${tag}${atts.join('')}${closing ? '/' : ''}>`;
}

function exactcase(parser, { name, value }, location) {
	const loc = location.attrs[name];
	if (loc) {
		const sub = parser.extract(loc.startOffset, loc.endOffset);
		return { name: sub.substr(0, name.length), value };
	} else {
		return { name, value };
	}
}

function resolveall(parser, tag, before) {
	return before.reduce((after, att) => {
		if (reserved(att.name)) {
			resolveone(parser, tag, after, att);
		} else {
			after.push(att);
		}
		return after;
	}, []);
}

function resolveone(parser, tag, atts, { name, value }) {
	const code = format(decode(value));
	const type = localname(name);
	switch (prefix(name)) {
		case 'on':
			updateatts(
				atts,
				'data-plastique-on',
				`${type}:${injectevent(parser, tag, code)}`
			);
			break;
		case 'do':
			updateatts(atts, `data-plastique-${type}`, inject(parser, code, type));
			break;
	}
}

sections.desc:
function resolveguid({ name, value }) { 
	name = name === 'guid' ? 'data-plastique-id' : name; 
	return { name, value }; 
}

sections.code:
function updateatts(atts, dataname, value) {
	let old = att => att.name === dataname;
	let att = atts.find(old);
	if (!att) {
		atts.push((att = { name: dataname, value: '' }));
	}
	att.value += (att.value ? ';' : '') + value;
}

function attributedone({ name, value }) {
	return name.startsWith('@') ? proxyatt(name.slice(1)) : ` ${name}="${value}"`;
}

function proxyatt(name) {
	return SAFEATT.test(name)
		? ' ${out.' + name + '}'
		: " ${out['" + name + "']}";
}

function injectevent(parser, tag, code) {
	const form = /textarea|input/.test(tag);
	const args = form ? '(e, value, checked)' : 'e';
	return inject(parser, code, args);
}

function inject(parser, code, args = '()') {
	const next = parser.increment();
	const call = `const df${next} = out.arrghs(${args} => ${code});\n`;
	parser.inject(call);
	return '${df' + next + '}';
}

function format(code, lead = '') {
	code = pretty(code);
	if (code.includes('\n')) {
		return indent(pretty('{' + code + '}'), lead);
	} else {
		return code.replace(/;$/, '');
	}
}

function pretty(code) {
	return prettier
		.format(code, {
			useTabs: true,
			singleQuote: true,
			printWidth: 1000
		})
		.trim();
}

function indent(code, lead) {
	return code
		.split('\n')
		.map((l, i) => (i ? lead + l : l))
		.join('\n');
}

import step1 from './step1';
import step2 from './step2';
import step3 from './step3';
import step4 from './step4';
import callsite from 'callsite';
import path from 'path';
import glob from 'glob';
import fs from 'fs';

export function compile(targets, options = {}) {
	const base = getsource(callsite());
	const list = gettargets(targets, base);
	const code = transform(list, options);
	return [...code].map(entry => write(...entry));
}

const stats = target => fs.lstatSync(target);
const exists = target => fs.existsSync(target);
const isfolder = target => exists(target) && stats(target).isDirectory();
const isfile = target => exists(target) && stats(target).isFile();
const isglob = target => !isfolder(target) && !isfile(target);
const getfiles = folder => glob.sync(path.normalize(folder + '/**/*.edbml'));

function gettargets(targets, base) {
	targets = makearray(targets);
	const folders = targets.filter(isfolder);
	const files = targets.filter(isfile);
	const globs = targets.filter(isglob);
	globs.forEach(pattern => files.push(...getfiles(glob.sync(pattern))));
	folders.forEach(folder => files.push(...getfiles(folder)));
	return makeabsolute([...new Set(files)], base);
}

function transform(files, options) {
	const map = new Map();
	const ext = options.extname || '.edbml.js';
	options = prettier(options);
	files.forEach(file => {
		const target = basename(file) + ext;
		const jscode = parse(read(file), options);
		if (jscode instanceof Error) {
			fail(jscode, file, read(file));
		} else {
			map.set(target, jscode);
		}
	});
	return map;
}

function fail(error, file, edbml) {
	console.log(`${error.message} in "${file}"\n`);
	console.log(
		edbml
			.split('\n')
			.map((line, i) => {
				return padStart(i + 1, 3, 0) + '   ' + line;
			})
			.join('\n')
	);
	process.exit(1);
}

function padStart(string, target, fill = ' ') {
	string = String(string);
	target = target >> 0;
	fill = String(fill);
	if (string.length > target) {
		return string;
	} else {
		target = target - string.length;
		if (target > fill.length) {
			fill += fill.repeat(target / fill.length);
		}
		return fill.slice(0, target) + string;
	}
}

function parse(edbml, options = {}) {
	return [step1, step2, step3, step4].reduce((result, step) => {
		return result instanceof Error ? result : step(result, options);
	}, edbml);
}

function prettier(options) {
	options.prettier = options.prettier || {
		useTabs: true,
		singleQuote: true,
		printWidth: 1000
	};
	return options;
}

function basename(file) {
	return file.slice(0, file.lastIndexOf(path.extname(file)));
}

function makeabsolute(files, base) {
	return files.map(file => {
		if (!path.isAbsolute(file)) {
			file = path.normalize(path.join(base, file));
		}
		return file;
	});
}

function getsource(stack) {
	return path.dirname(stack[1].getFileName());
}

function read(file) {
	return fs.readFileSync(file, { encoding: 'UTF-8' });
}

function write(file, code) {
	if (code !== null) {
		fs.writeFileSync(file, code);
	}
	return file;
}

function makearray(thing) {
	return Array.isArray(thing) ? thing : [thing];
}

export default function(script, options) {
	return [prepare, parse, restore].reduce((code, step) => {
		return code instanceof Error ? code : step(code, options);
	}, script);
}

function prepare(script) {
	let html = false;
	let cont = false;
	let lead = '';
	let adds = line => line.endsWith('+');
	let atts = line => {
		return line.replace(NORMALS, 'out.$1 =').replace(SPECIAL, "out['$1'] =");
	};
	return script
		.split('\n')
		.map(line => {
			lead = /^\s*/.exec(line);
			line = line.trim();
			switch (line[0]) {
				case '<':
					html = true;
					cont = adds(line);
					break;
				case '+':
					cont = html;
					break;
				case '@':
					if (!cont) {
						line = atts(line);
						html = false;
					}
					break;
				default:
					if (cont) {
						cont = adds(line);
					} else {
						html = false;
					}
					break;
			}
			return (html ? '//!' : '') + lead + line;
		})
		.join('\n');
}

function parse(script) {
	let error = null;
	script = prettier.format(script, {
		useTabs: true,
		singleQuote: true,
		printWidth: 1000,
		parser(text, { babylon }) {
			let ast;
			try {
				ast = babylon(text);
			} catch (exception) {
				ast = babylon('');
				error = exception;
			}
			return strip(ast);
		}
	});
	return error || script;
}

function strip(ast) {
	ast.comments.forEach(c => {
		if (!c.value.startsWith('!')) {
			c.value = '?';
		}
	});
	return ast;
}

function restore(script) {
	const block = /\/\*\?\*\//g;
	const lines = /\/\/\?/g;
	return [block, lines].reduce((result, regex) => {
		return result.replace(regex, '');
	}, script);
}

export default function(script) {
	return script.split('\n').reduce(reducer, []);
}

function reducer(result, line) {
	const was = !!result.now;
	if ((result.now = MARKUP.test(line))) {
		if (was) {
			result[result.length - 1] += strip(line);
		} else {
			result.push(strip(line, true));
		}
	} else {
		result.push(line);
	}
	return result;
}

function strip(line, first) {
	const LEAD = /^\+/;
	const ENDS = /\+$/;
	return first
		? line.trimRight().replace(ENDS, '')
		: line
				.replace(MARKUP, '')
				.trim()
				.replace(LEAD, '')
				.replace(ENDS, '');
}

export default function(lines) {
	const parser = new Parser();
	lines = lines.map(lineparser(parser));
	return inject(lines, parser.injections());
}

function lineparser(parser) {
	return (line, index) => {
		const ishtml = MARKUPCOMMENT.test(line);
		if (ishtml) {
			line = line.replace(MARKUPCOMMENT, '');
			line = parseedbml(line, parser);
		}
		parser.reset(ishtml, index);
		return line;
	};
}

function parseedbml(line, parser) {
	const lead = /^\s*/.exec(line);
	line = line.trim();
	line = line.replace(/^\+/, '');
	line = line.replace(/\+$/, '');
	line = parser.parse(line);
	return lead + 'out`' + line + '`;';
}

function inject(lines, map) {
	return lines.reduce((lines, line, index) => {
		lines.push(line);
		if (map.has(index)) {
			lines.push(...map.get(index));
		}
		return lines;
	}, []);
}

export default function(lines, options) {
	return [functions, imports, cleanup].reduce((result, step) => {
		return step(result, options);
	}, lines);
}

function functions(lines) {
	let main = [];
	let func = false;
	let body = null;
	let name = null;
	let expo = false;
	lines.forEach(line => {
		if (func) {
			if (line.trimRight() === '}') {
				main.push(resolve(body, name, expo), line);
				func = false;
			} else {
				body.push(line);
			}
		} else {
			if (FUNCTION.test(line)) {
				func = true;
				body = [];
				name = FUNCNAME.exec(line)[1];
				expo = line.trimLeft().startsWith('export');
				main.push(line);
			} else {
				main.push(line);
			}
		}
	});
	return main.join('\n');
}

function resolve(body, name, expo) {
	const out = expo || body.some(l => /out`|out\(/.test(l));
	const intro = out ? `\tconst out = output();` : '';
	const extro = expo ? `\treturn out();` : '';
	return [intro, ...body, extro].join('\n');
}

function imports(script) {
	const list = /out`|out\(/g.test(script) ? ['output'] : [];
	const head = list[0]
		? `import { ${list.join(', ')} } from '${RUNTIME}';`
		: '';
	return [head, script].join('\n\n');
}

function cleanup(script, options) {
	const notempty = line => line.trim() !== '';
	return prettier.format(
		script
			.split('\n')
			.filter(notempty)
			.join('\n'),
		options
	);
}

export function htmlattribute(target, name, value, type = gettype(value)) {
	switch (type) {
		case 'null':
		case 'undefined':
			return '';
		case 'object':
			return getall(target, Object.entries(value));
		case 'array':
			return getall(value); 
		default:
			return getone(target, name, value, type);
	}
}

function getone(target, name, val, type) {
	const unsafe = encode(val, type || gettype(val));
	return name + `="${target.guargh(unsafe)}"`;
}

function getall(target, entries) {
	return entries
		.map(entry => getone(target, ...entry))
		.join(' ')
		.trim();
}

function gettype(val) {
	const stringify = Object.prototype.toString;
	const typematch = /\s([a-zA-Z]+)/;
	return stringify
		.call(val)
		.match(typematch)[1]
		.toLowerCase();
}

function encode(val, type) {
	switch (type) {
		case 'string':
			return val;
		case 'number':
		case 'boolean':
			return String(val);
			break;
		case 'date':
			throw new Error('TODO: Encode standard date format?');
		default:
			throw new Error('Could not create HTML attribute from: ' + type);
	}
}

const [EVENTS, ELEMENTS, SPIRITS] = [
	'data-plastique-on', 
	'data-plastique-elm', 
	'data-plastique-gui' 
];

export function mapdefer(guids, defers) {
	guids = translate(guids);
	return [
		isolateevents(isolate(EVENTS, guids), defers),
		isolate(ELEMENTS, guids, defers),
		isolate(SPIRITS, guids, defers)
	];
}

function translate(guids) {
	const result = new Map();
	guids.forEach((elm, guid) => extractall(elm, guid, result));
	return result;
}

function isolate(type, guids, defers) {
	const result = new Map();
	guids.forEach((map, guid) => {
		if (map.has(type)) {
			const key = map.get(type);
			const val = defers ? defers.get(key) : key;
			result.set(guid, val);
		}
	});
	return result;
}

function extractall(elm, guid, result) {
	const atts = elm.attributes;
	for (let i = atts.length - 1; i >= 0; i--) {
		extractone(elm, guid, result, atts.item(i));
	}
}

function extractone(elm, guid, result, { name, value }) {
	if (reserved.has(name)) {
		recordone(guid, result, name, value);
		elm.removeAttribute(name);
	}
}

function recordone(guid, result, name, value) {
	getrecord(guid, result).set(name, value);
}

function getrecord(guid, result) {
	return result.get(guid) || result.set(guid, new Map()).get(guid);
}

function isolateevents(guids, defers) {
	if (guids.size) {
		const lookup = ([type, key]) => [type, change(key)];
		const change = key => changefun(defers.get(key));
		guids.forEach((key, guid) => {
			guids.set(guid, new Map(splitup(key, defers, lookup)));
		});
	}
	return guids;
}

function splitup(string, defers, lookup) {
	return string
		.split(';')
		.map(sliceup)
		.map(lookup);
}

function sliceup(part) {
	const i = part.lastIndexOf(':');
	return [part.substring(0, i), part.substring(i + 1)];
}

function changefun(fun) {
	return function(e) {
		if (/input|textarea/.test(this.localName)) {
			fun(e, this.value, this.checked);
		} else {
			fun(e);
		}
	};
}

export function fixvalue(value, unsafe, nodes) {
	const attribute = /^\S+=".*"$/g;
	if (value) {
		if (isnode(value)) {
			const marker = `{node:${nodes.length}}`;
			nodes.push(value);
			return marker;
		} else {
			const marker = `{unsafe:${unsafe.length}}`;
			unsafe.push(value);
			return marker;
		}
	}
	return '';
}

export function fixtext(data, unsafe) {
	return fix(data, unsafe);
}

export function fixattr(name, value, unsafe) {
	return safeattr(name, fix(value, unsafe));
}

function isnode(value) {
	return !!value.nodeType || value.isModel || value.isSpirit;
}

function fix(data, unsafe) {
	return data
		.split(/{unsafe:(\d+)}/)
		.map((string, index) => {
			return index % 2 === 0 ? string : unsafe[parseInt(string, 10)];
		})
		.join('');
}

import { mapguids } from '@dataplastique/util';
import { runnable } from './runnable';
import { htmlattribute } from './attributes';
import { normalize, canonize } from './normalize';
import { mapdefer } from './deferred';

constructor(markup = '', unsafe = [], others = [], defers = []) {
	this[state] = { markup, unsafe, others, defers };
	props.clear();
}

toString(guid) {
	return resolve(this, guid).innerHTML;
}

toFragment(guid) {
	return resolve(this, guid).content;
}

toAspects(guid) {
	const cloned = resolve(this, guid, true);
	const defers = this[state].defers;
	return aspects(cloned, guid, defers);
}

deferred() {
	return this[state].defers;
}

similar(out) {
	return out[state].markup === this[state].markup;
}

identical(out) {
	const mine = this[state];
	const other = out[state];
	return (
		this.similar(out) &&
		equals(mine.unsafe, other.unsafe) &&
		equals(mine.others, other.others)
	);
}

update(out) {
	if (this.similar(out)) {
		Object.assign(this[state], out[state]);
	} else {
		throw new Error('Output must be structurally similar.');
	}
	return this;
}

static get(out, name) {
	switch (name) {
		case 'guargh':
		case 'blargh':
		case 'arrghs':
			return out[name];
			break;
		default:
			return out.blargh(htmlattribute(out, name, props.get(name)));
			break;
	}
}

static set(out, name, value) {
	props.set(name, value);
	return true;
}

	static apply(out, that, args) {
		return out.apply(that, args);
	}
}

function resolve(out, guid, canon = false) {
	const clone = collapse(out, out[state]);
	return canon ? canonize(clone, guid) : normalize(clone, guid);
}

function collapse(out, state) {
	return (out._run || (out._run = runnable(state)))(state);
}

function aspects(canonical, guid, defers) {
	const fragment = canonical.content;
	const rootelem = fragment.firstElementChild;
	const elements = mapguids(rootelem, guid);
	const deferred = mapdefer(elements, defers);
	return [fragment, elements, ...deferred];
}
// TODO: Deprecate this? .......................................................

function equals(a1, a2) {
	const same = (value, idx) => a2[idx] === value;
	return a1.length === a2.length && a1.every(same);
}

export function normalize(temp, guid) {
	const frag = temp.content;
	expand(first(frag), guid);
	return temp;
}

export function canonize(temp, guid) {
	temp = normalize(temp, guid);
	rootguid(temp.content, guid);
	return temp;
}

function expand(elm, guid) {
	if (elm) {
		expand(first(elm), guid);
		expand(next(elm), guid);
		if (hasguid(elm)) {
			setguid(elm, guid, getguid(elm));
			delguid(elm);
		}
	}
}

function rootguid(frag, guid) {
	const root = first(frag);
	if (isroot(root)) {
		delguid(root);
		setguid(root, guid);
		return frag;
	} else {
		return rootguid(createroot(frag), guid);
	}
}

function createroot(frag) {
	let node;
	const root = newelm('this');
	while ((node = frag.firstChild)) {
		append(root, node);
	}
	append(frag, root);
	return frag;
}

function setguid(elm, rootguid, elemguid) {
	elm.dataset.plastiqueId = rootguid + (elemguid ? `-${elemguid}` : '');
}

export function runnable({ markup }) {
	const elm = template(markup);
	const map = index(elm.content);
	return function run({ unsafe, others }) {
		resolve(map, unsafe);
		return template(elm.innerHTML); 
	};
}

function template(markup) {
	const elm = document.createElement('template');
	elm.innerHTML = markup;
	return elm;
}

function index(node, map = new Map()) {
	if (node) {
		index(node.firstChild, map);
		index(node.nextSibling, map);
		switch (node.nodeType) {
			case Node.TEXT_NODE:
				if (node.data.trim() !== '') {
					if (node.data.includes('{unsafe:')) {
						map.set(node, node.data);
					}
				}
				break;
			case Node.ELEMENT_NODE:
				Array.from(node.attributes)
					.filter(att => att.value.includes('{unsafe:'))
					.forEach(att => map.set(att, att.value));
				break;
		}
	}
	return map;
}

function resolve(map, unsafe) {
	map.forEach((data, node) => {
		if (node.nodeType === Node.TEXT_NODE) {
			node.data = fixtext(data, unsafe);
		} else {
			node.value = fixattr(node.name, data, unsafe);
		}
	});
}

function build(node, others) {
	const frag = document.createDocumentFragment();
	const cuts = node.data.split(/{node:(\d+)}/);
	const elem = n => (n.isSpirit ? n.element : n);
	cuts.forEach(part => {
		if (part.length) {
			const index = parseInt(part, 10);
			if (isNaN(index)) {
				frag.appendChild(document.createTextNode(part));
			} else {
				const other = others[index];
				frag.appendChild(elem(other));
			}
		}
	});
	return frag;
}

export function safeattr(name, value) {
	return String(value).replace(unsafexp, c => safemap[c]);
}

const rooted = html => `<wellformed>${html}</wellformed>`;
const unroot = html => html.replace(/<\/*wellformed>/g, '');
const encode = html => html.replace(/&([^\s]*);/g, '&amp;$1;');
const decode = html => html.replace(/&amp;([^\s]*);/g, '&$1;');
let serial = null;
let parser = null;

export function wellformed(markup) {
	parser = parser || (parser = new DOMParser());
	const xml = encode(rooted(markup));
	const doc = parser.parseFromString(xml, 'application/xhtml+xml');
	const err = doc.querySelector('parsererror');
	if (err) {
		serial = serial || (serial = new XMLSerializer());
		markup = serial.serializeToString(doc.documentElement);
		markup = decode(unroot(markup));
	}
	return markup;
}

export function similar(oldn, newn) {
	return oldn && newn && samenode(oldn, newn)
		? istext(oldn) || (samesize(oldn, newn) && samekids(oldn, newn))
		: false;
}

export function samesize(elm1, elm2) {
	return elm1.childNodes.length === elm2.childNodes.length;
}

export function samenode(n1, n2) {
	return sametype(n1, n2)
		? iselm(n1)
			? samename(n1, n2) && getguid(n1)
				? sameguid(n1, n2)
				: sameatts(n1, n2)
			: sametext(n1, n2)
		: false;
}

export function samekids(node1, node2) {
	const [kids1, kids2] = [children(node1), children(node2)];
	return kids1.every((kid1, i) => samenode(kid1, kids2[i]));
}

export function sameguid(elm1, elm2) {
	return getguid(elm1) === getguid(elm2);
}

export function sameatts(elm1, elm2) {
	return compatts(elm1, elm2) && compatts(elm2, elm1);
}

export function allguids(oldn, newn) {
	return iselm(oldn) && iselm(newn)
		? do {
				const oldkids = subguids(oldn);
				const newkids = subguids(newn);
				nonulls(oldkids) && nonulls(newkids)
					? do {
							true;
					  }
					: false;
		  }
		: false;
}

export function updateevents(root, guid, ofun, nfun, oids, nids, nmap, omap) {
	let handler = handlers.get(root);
	if (handler) {
		removeevents(ofun, nfun, oids, nids, nmap, omap, handler);
		handler.map = nfun;
	} else {
		handler = new Handler(nfun, guid);
		handlers.set(root, handler);
	}
	addevents(nfun, nmap, handler);
}

class Handler {
	map = null;
	constructor(map, guid) {
		this.map = map;
		this.guid = guid;
	}
	handleEvent(e) {
		const elem = e.currentTarget;
		const guid = elem.dataset.plastiqueId;
		const type = this.map.get(guid || this.guid);
		const func = type.get(e.type);
		if (func) {
			func.call(elem, e);
		}
	}
}

function removeevents(ofun, nfun, oids, nids, nmap, omap, handler) {
	omap.forEach((elm, guid) => {
		if (ofun.has(guid)) {
			const oats = ofun.get(guid);
			const nats = nfun.get(guid);
			const gone = !nmap.has(guid);
			oats.forEach((value, type) => {
				if (gone || (!nats || !nats.has(type))) {
					elm.removeEventListener(type, handler);
				}
			});
		}
	});
}

function addevents(nfun, nmap, handler) {
	nmap.forEach((elm, guid) => {
		if (nfun.has(guid)) {
			nfun.get(guid).forEach((func, type) => {
				elm.addEventListener(type, handler);
			});
		}
	});
}

const istype = (node, type) => node && node.nodeType === type;
const istext = node => istype(node, Node.TEXT_NODE);
const iswhite = node => istext(node) && node.data.trim() === '';
const unwhite = node => !iswhite(node);

function purge(n) {
	return n && iswhite(n) ? purge(n.nextSibling) : iswhite(n) ? null : n;
}

export function snapshotbefore(root) {
	const elm = root.activeElement || document.activeElement;
	if (elm && root.contains(elm)) {
		return elm.dataset.plastiqueId ? elm : selector(root, elm);
	}
}

export function restoreafter(root, elm) {
	const now = root.activeElement || document.activeElement;
	if (elm && (elm = elm.nodeType ? elm : root.querySelector(elm))) {
		if (root.contains(elm) && elm !== now) {
			elm.focus();
		}
	}
}

function selector(root, elm) {
	const parts = [];
	const [ELEM, FRAG] = [Node.ELEMENT_NODE, Node.DOCUMENT_FRAGMENT_NODE];
	while (elm && (elm.nodeType === ELEM || elm.nodeType === FRAG)) {
		if (elm.id) {
			parts.push('#' + elm.id);
			elm = null;
		} else {
			if (elm === root) {
				parts.push(':scope ');
				elm = null;
			} else {
				parts.push(` > ${elm.localName}:nth-child(${ordinal(elm)})`);
				elm = elm.parentNode;
			}
		}
	}
	return parts.reverse().join('');
}

function ordinal(elm) {
	let result = 1;
	let parent = elm.parentNode;
	if (parent) {
		let node = parent.firstElementChild;
		while (node && node !== elm) {
			node = node.nextElementSibling;
			result++;
		}
	}
	return result;
}

function silently(type, elm) {
	const cb = e => e.stopPropagation();
	elm.addEventListener(type, cb, true);
	elm[type]();
	elm.removeEventListener(type, cb, true);
	return elm;
}

import { mapguids } from '@dataplastique/util';
import { updateevents } from './events';
import { snapshotbefore, restoreafter } from './focus';
import { removekids, replacenode, replacekids } from './mutate';
import { childnode, nextnode, childelm, nextelm } from './filter';
import {
	similar,
