export function getdiffs(root, guid, newout, oldout) {
	const [ndoc, nids, nfun, doelm, dogui] = newout.toAspects(guid);
	const [odoc, oids, ofun] = oldout.toAspects(guid);
	return [
		...treediffs(root, guid, odoc, ndoc, oids, nids),
		...miscdiffs(root, guid, oids, nids, ofun, nfun, doelm, dogui)
	];
}

export function patchdiffs(root, guid, diffs) {
	const log = Object.create(null);
	if (diffs.length) {
		const range = document.createRange();
		const focus = snapshotbefore(root);
		const guids = mapguids(root, guid);
		diffs.forEach(diff => diff(range, guids));
		restoreafter(root, focus);
		range.detach();
	}
	return log;
}

function treediffs(root, guid, odoc, ndoc, oids, nids) {
	return oids.size > 1 && odoc.hasChildNodes()
		? do {
				const updates = trawl(oids, odoc, ndoc, guid);
				const reduced = reduce(updates, oids);
				const actions = getactions(oids, nids);
				compile(reduced, actions);
		  }
		: [
				range => {
					const olde = oids.get(guid);
					const newe = nids.get(guid);
					replacekids(range, root, newe);
					syncattribs(root, olde, newe);
				}
		  ];
}

function compile(updates, actions) {
	return updates.map(({ type, guid, operations }) => (range, xids) => {
		return actions[type](range, xids, guid, operations);
	});
}

function trawl(oids, oldn, newn, aguid, pguid, updates = []) {
	const [child, next, guid] = [childnode, nextnode, getguid];
	if (oldn || newn) {
		if (similar(oldn, newn)) {
			if (sameguid(oldn, newn) && !sameatts(oldn, newn)) {
				updates.push(attsupdate(guid(oldn)));
			}
			trawl(
				oids,
				child(oldn),
				child(newn),
				guid(oldn) || aguid,
				guid(oldn),
				updates
			);
			trawl(oids, next(oldn), next(newn), aguid, pguid, updates);
		} else if (pguid && sameguid(oldn, newn) && allguids(oldn, newn)) {
			if (softqualify(oids, oldn, newn)) {
				updates.push(
					softupdate(guid(oldn), oldn, newn),
					attsupdate(guid(oldn))
				);
				trawlon(oids, submap(oldn), submap(newn), updates);
			} else {
				updates.push(hardupdate(aguid), attsupdate(aguid));
			}
		} else {
			updates.push(hardupdate(aguid), attsupdate(aguid));
		}
	}
	return updates;
}

function trawlon(oids, oldmap, newmap, updates) {
	newmap.forEach((newn, guid) => {
		const oldn = oldmap.get(guid);
		if (oldn) {
			trawl(oids, childnode(oldn), childnode(newn), guid, guid, updates);
		}
	});
}

function reduce(updates, oids) {
	const elem = ({ guid }) => oids.get(guid);
	const hard = ({ type }) => type === 'hardupdate';
	const pass = n => h => h === n || !h.contains(n);
	const list = updates.filter(hard).map(elem);
	return updates.reduce((subset, update) => {
		if (list.every(pass(elem(update)))) {
			subset.push(update);
		}
		return subset;
	}, []);
}

function hardupdate(range, xids, guid) {
	const xelm = xids.get(guid);
	const olde = oids.get(guid);
	const newe = nids.get(guid);
	if (xelm) {
		patchstart(range, xelm, newe, oids, nids, xids);
	}
}

function attsupdate(range, xids, guid) {
	const xelm = xids.get(guid);
	const olde = oids.get(guid);
	const newe = nids.get(guid);
	if (xelm) {
		syncattribs(xelm, olde, newe);
	}
}

	function softupdate(range, xids, guid, operations) {
		updatesoft(guid, xids, nids, operations);
	}
	return { hardupdate, attsupdate, softupdate };
}
// Diffing more stuff! .........................................................
function miscdiffs(root, guid, oids, nids, ofun, nfun, doelm, dogui) {
	return [
		(range, oldmap) => {
			const newmap = mapguids(root, guid);
			[
				...getcallbackdiffs(root, guid, ofun, nfun, oids, nids),
				...getsetandcall(root, guid, doelm, dogui)
			].forEach(diff => diff(newmap, oldmap));
		}
	];
}

function getcallbackdiffs(root, guid, ofun, nfun, oids, nids) {
	return [
		(newmap, oldmap) => {
			updateevents(root, guid, ofun, nfun, oids, nids, newmap, oldmap);
		}
	];
}

function getsetandcall(root, guid, doelm, dogui) {
	const proplist = [...doelm.entries()];
	const calllist = [...dogui.entries()];
	const update = (newmap, [guid, action], iselm) => {
		const xelm = newmap.get(guid);
		iselm ? action(xelm) : console.warn('TODO: do:gui');
	};
	return [
		...proplist.map(up => newmap => update(newmap, up, true)),
		...calllist.map(up => newmap => update(newmap, up, false))
	];
}

function patchstart(range, xelm, newn, oids, nids, originals) {
	replacekids(range, xelm, newn, oids, nids, originals);
	restoreoriginals(range, xelm, oids, nids, originals);
}

function restoreoriginals(range, elm, oids, nids, originals) {
	const changelings = mapguids(elm);
	changelings.forEach((changeling, guid) => {
		if (originals.has(guid)) {
			const original = originals.get(guid);
			if (changeling !== original) {
				swapchangeling(range, changeling, original, oids, nids);
			}
		}
	});
}

function swapchangeling(range, changeling, original, oids, nids) {
	const guid = getguid(original);
	const olde = oids.get(guid);
	const newe = nids.get(guid);
	removekids(range, original);
	replacenode(changeling, original);
	replacekids(range, original, changeling);
	syncattribs(original, olde, newe);
}

function syncattribs(elm, oldn, newn) {
	if (hasatts(oldn) || hasatts(newn)) {
		const oatts = mapatts(oldn);
		const natts = mapatts(newn);
		const names = ['class', 'data-plastique-id'];
		const skips = name => names.some(n => name === n);
		natts.forEach((v, n) => (skips(n) ? void 0 : setatt(elm, n, v)));
		oatts.forEach(
			(v, n) => (skips(n) || natts.has(n) ? void 0 : delatt(elm, n))
		);
		if (hasclass(oldn) || hasclass(newn)) {
			syncclass(classes(elm), classes(oldn), classes(newn));
		}
	}
}

function syncclass(list, olist, nlist) {
	const added = c => !olist || !olist.contains(c);
	const nuked = c => !nlist.contains(c);
	asarray(nlist)
		.filter(added)
		.forEach(c => list.add(c));
	if (olist) {
		asarray(olist)
			.filter(nuked)
			.forEach(c => list.remove(c));
	}
}

export function removekids(range, elm) {
	range.selectNodeContents(elm);
	range.deleteContents();
}

export function replacenode(oldn, newn) {
	const parent = oldn.parentNode;
	parent.replaceChild(newn, oldn);
	return newn;
}

export function replacekids(range, elm, other) {
	removekids(range, elm);
	range.selectNodeContents(other);
	elm.appendChild(range.extractContents());
}

export function hardupdate(guid) {
	return { type: 'hardupdate', guid };
}

export function attsupdate(guid) {
	return { type: 'attsupdate', guid };
}

export function softupdate(guid, oldn, newn) {
	return { type: 'softupdate', guid, operations: getoperations(oldn, newn) };
}

export function softqualify(oids, oldn, newn) {
	const { oldkids, newkids } = breakdown(oldn, newn);
	const moved = guid => oldkids.includes(guid);
	const isnew = guid => !oids.has(guid) || moved(guid);
	return newkids.every(isnew);
}

export function updatesoft(guid, xids, nids, operations) {
	operations.forEach(op => executesoft(guid, xids, nids, op));
}

export function breakdown(oldn, newn) {
	const [oldkids, newkids] = [childguids(oldn), childguids(newn)];
	const [addlist, outlist] = [uniq(newkids, oldkids), uniq(oldkids, newkids)];
	const remains = uniq(oldkids, outlist);
	const results = remains.concat(newkids);
	return { oldkids, newkids, addlist, outlist, remains, results };
}

const uniq = (a1, a2) => a1.filter(e => !a2.includes(e));
const getguid = elm => elm.getAttribute('data-plastique-id');
const childguids = elm => Array.from(elm.children).map(getguid);

const operations = (operation => ({
	remove: guid => operation('remove', guid),
	move: (guid, prev) => operation('move', guid, prev),
	insert: (guid, prev) => operation('insert', guid, prev)
}))((type, guid, prev) => ({ type, guid, prev }));

function getoperations(oldn, newn) {
	const { newkids, addlist, outlist, remains } = breakdown(oldn, newn);
	return newkids.reduce(
		getreducer(remains, addlist),
		outlist.map(operations.remove)
	);
}

function getreducer(remains, addlist) {
	return (ops, newguid, i) => {
		const oldguid = remains[i];
		const preguid = remains[i - 1];
		if (oldguid !== newguid) {
			remains.splice(i, 0, newguid);
			ops.push(
				addlist.includes(newguid)
					? operations.insert(newguid, preguid)
					: operations.move(newguid, preguid)
			);
		}
		return ops;
	};
}

function executesoft(parent, xids, nids, { type, guid, prev }) {
	return executions[type](parent, guid, prev, xids, nids, guid);
}

function remove(parent, guid, prev, xids) {
	xids.get(guid).remove();
}

function move(parent, guid, prev, xids) {
	const oldnode = xids.get(guid);
	if (oldnode.parentNode === (parent = xids.get(parent))) {
		if (prev) {
			prev = xids.get(prev);
			parent.insertBefore(oldnode, prev.nextSibling);
		} else {
			parent.prepend(oldnode);
		}
	}
}

	function insert(parent, guid, prev, xids, nids) {
		parent = xids.get(parent);
		const newnode = nids.get(guid);
		if (prev) {
			prev = xids.get(prev) || nids.get(prev);
			parent.insertBefore(newnode, prev.nextSibling);
		} else {
			parent.prepend(newnode);
		}
	}
	return { remove, move, insert };
})();

import Out from './Out/';
import { fixvalue } from './Out/fixes';
import { getdiffs, patchdiffs } from './diffengine/';

export function output() {
	return first ? nextout : (first = firstout());
}

export function diff(root, guid, newout, oldout) {
	return getdiffs(root, guid, newout, oldout || new Out());
}

export function patch(root, guid, diffs, calls) {
	return patchdiffs(root, guid, diffs, calls);
}

function firstout(defer) {
	let markup = '';
	let unsafe = [];
	let others = [];
	let defers = new Map();
	let bypass = new Set();
	function out(arg, ...args) {
		if (arguments.length) {
			markup += replace(arg, args, unsafe, others, bypass);
			bypass.clear();
		} else {
			return finalize(markup, unsafe, others, defers);
		}
	}
	return proxy(out, {
		defer: defer,
		blargh: dec => {
			bypass.add(dec);
			return dec;
		},
		guargh: val => {
			unsafe.push(val);
			return fixvalue(val, unsafe, others);
		},
		arrghs: fun => {
			const key = `cb${generatekey()}`;
			defers.set(key, fun);
			return key;
		}
	});
}

const nextout = (function(out) {
	return proxy(out, {
		blargh: dec => (first ? first.blargh(dec) : ''),
		guargh: val => (first ? first.guargh(val) : ''),
		arrghs: fun => (first ? first.arrghs(fun) : '')
	});
})(function out(arg, ...args) {
	if (arguments.length) {
		first(...arguments);
	} else {
		return '';
	}
});

function proxy(out, expandos) {
	return new Proxy(Object.assign(out, expandos), Out);
}

function passtonext(defer) {
	nextout.defer = first.defer;
	return nextout;
}

function generatekey(fix = 'cb') {
	return String(Math.random()).slice(2, 11);
}

function nodefer() {
	throw new Error(
		`In order to support deferred callbakcs, the EDBML function 
		must be stamped with a method "defer()" before it is called.`
	);
}

function replace(strings, values, unsafe, others, bypass) {
	return Array.isArray(strings) && strings.raw
		? strings.raw.reduce((result, string, i) => {
				return result + string + johnson(values[i], unsafe, others, bypass);
		  }, '')
		: String(strings);
}

function johnson(value, unsafe, others, bypass) {
	return bypass.has(value) && value.includes('=')
		? value
		: fixvalue(value, unsafe, others);
}

function finalize(markup, unsafe, others, defers) {
	first = null;
	nextout.defer = null;
	return new Out(markup, unsafe, others, defers);
}

