export function boot(config = {}) {
	return new Promise((resolve, reject) => {
		boot.done
			? resolve()
			: do {
					document.readyState === 'loading'
						? reject(`Premature boot: Await DOMContentLoaded`)
						: do {
								Config.init(config);
								bootstrap(document);
								boot.done = true;
								resolve();
						  };
			  };
	});
}

