			expect(last).toBe(undefined);
			expect(col.length).toBe(5);
			let first = col.shift();
			expect(first).toBe(1);
			expect(col.length).toBe(4);
		});
		it('should support simple objects and arrays', () => {
			let simpleo = o => o.constructor === Object;
