const fixImmediate = (() => {
	let index = -1;
	if (Environment.browser) {
		let runs = false;
		const list = [];
		const node = document.createElement('div');
		new MutationObserver(() => {
			runs = false;
			while (list.length) {
				list.shift()();
			}
		}).observe(node, {
			attributes: true
		});
		return action => {
			const id = ++index;
			list.push(action);
			if (!runs) {
				node.classList.toggle('trigger');
			}
			return id;
		};
	}
})();

