}

@chained
static start(type, time) {
	if (!intervals.has(type)) {
		intervals.set(
			type,
			setInterval(() => {
				dispatch(type);
			}, time)
		);
	}
}

static schedule(cb) {
	return Environment.browser ? this.nextFrame(cb) : this.time(cb);
}

@chained
static cancelSchedule(id) {
	Environment.browser ? this.cancelFrame(id) : this.cancelTime(id);
}

@chained
static stop(type) {
	if (intervals.has(type)) {
		clearTimeout(intervals.get(type));
	}
}

	@chained
	static dispatch(type, time) {
		dispatch(type, time);
	}
}

function get(type) {
	if (!pool.has(type)) {
		pool.set(type, new Tick(type));
	}
	return pool.get(type);
}

function addhandler(type, handler, one) {
	asarray(type, t => {
		allhandlers.add(t, handler);
		if (one) {
			onehandlers.add(t, handler);
		}
	});
}

function delhandler(type, handler) {
	asarray(type, t => {
		allhandlers.del(type, handler);
		onehandlers.del(type, handler);
	});
}

function dispatch(type, time) {
	const now = arguments.length === 1;
	const set = scheduled;
	if (!set.has(type)) {
		set.add(type);
		const doit = () => {
			set.delete(type);
			handlers(get(type));
		};
		return now ? nexttick(doit) : setTimeout(doit, time);
	}
}

function handlers(tick) {
	const type = tick.type;
	const all = allhandlers;
	const one = onehandlers;
	all
		.toArray(type)
		.filter(handler => {
			handler.ontick(tick);
			return one.has(type, handler);
		})
		.forEach(handler => {
			all.del(type, handler);
			one.del(type, handler);
		});
}

function nexttick(action) {
	return Environment.browser
		? fixImmediate(action)
		: Environment.node
			? setImmediate(action)
			: todo('WebWorkers unite');
}

function todo(message = 'fix') {
	throw new Error('TODO: ' + message);
}

const [CLASS_SPEC, CLASS_BABEL, CLASS_CUSTOM] = [
	/^\s*class\s+/, 
	/_class\S+/i, 
	/^\[class / 
];

export function typeOf(any) {
	return stringify
		.call(any)
		.match(TYPE)[1]
		.toLowerCase();
}

export function isDefined(any) {
	return any !== undefined;
}

export function isComplex(any) {
	switch (any) {
		case undefined:
		case null:
		case true:
		case false:
			return false;
	}
	switch (typeof any) {
		case 'string':
		case 'symbol':
		case 'number':
			return false;
	}
	return true;
}

export function isArray(any) {
	return Array.isArray(any);
}

export function isFunction(any) {
	return !!(typeof any === 'function' && any.call && any.apply);
}

export function isObject(any) {
	return typeof any === 'object' && !Array.isArray(any);
}

export function isString(any) {
	return typeof any === 'string';
}

export function isNumber(any) {
	return typeof any === 'number';
}

export function isBoolean(any) {
	return any === true || any === false;
}

export function isDate(any) {
	return something(any) && any instanceof Date;
}

export function isNull(any) {
	return any === null;
}

export function isArguments(any) {
	return typeOf(any) === 'arguments';
}

export function isSymbol(any) {
	return typeof any === 'symbol';
}

export function isFile(any) {
	return typeOf(any) === 'file';
}

export function isWindow(any) {
	return !!(
		any &&
		any.document &&
		any.location &&
		any.alert &&
		any.setInterval
	);
}

export function isEvent(any) {
	return !!(typeOf(any).endsWith('event') && any.type);
}

export function isNode(any) {
	return !!(
		any &&
		typeof any === 'object' &&
		any.nodeType &&
		any.compareDocumentPosition
	);
}

export function isElement(any) {
	return isNode(any) && any.nodeType === Node.ELEMENT_NODE;
}

export function isDocumentFragment(any) {
	return isNode(any) && any.nodeType === Node.DOCUMENT_FRAGMENT_NODE;
}

export function isDocument(any) {
	return isNode(any) && any.nodeType === Node.DOCUMENT_NODE;
}

export function isClass(any) {
	if (any && typeof any === 'function') {
		const string = any.toString();
		const regexs = [CLASS_SPEC, CLASS_BABEL, CLASS_CUSTOM];
		return regexs.some(regexp => regexp.test(string));
	}
	return false;
}

export function isArrayLike(any) {
	return any && '0' in any && !isArray(any);
}

export function cast(string) {
	const s = String(string).trim();
	switch (s) {
		case 'null':
			return null;
		case 'undefined':
			return undefined;
		case 'true':
		case 'false':
			return s === 'true';
		default:
			return String(parseInt(s, 10)) === s
				? parseInt(s, 10)
				: String(parseFloat(s)) === s
